import React from 'react';
import PropTypes from 'prop-types';
import Header from '../components/Header';
import Carregando from '../components/Carregando';
import semImagem from '../images/semImagem.png';
import { getUser, updateUser } from '../services/userAPI';

class ProfileEdit extends React.Component {
  state = {
    image: '',
    name: '',
    disabled: true,
    email: '',
    descricao: '',
    carregando: true,
  }

  componentDidMount() {
    getUser().then(({ description, email, image, name }) => {
      const regex = /\S+@\S+\.\S+/;
      const isNameEmail = regex.test(name);
      this.setState({
        carregando: false,
        description,
        email: !isNameEmail ? email : name,
        image,
        name: isNameEmail ? '' : name,
      });
    });
  }

  componentDidUpdate() {
    const { disabled } = this.state;
    if (disabled) {
      this.ableButton();
    }
  }

  ableButton = () => {
    const { description, email, image, name } = this.state;
    const regex = /\S+@\S+\.\S+/;

    if (description && regex.test(email) && image && name) {
      this.setState({ disabled: false });
    }
  }

  handleChange = ({ target: { name, value } }) => {
    this.setState({
      [name]: value,
    });

    this.ableButton();
  }

  handleClick = (event) => {
    event.preventDefault();

    const { history } = this.props;

    const { description, email, image, name } = this.state;

    updateUser({ description, email, image, name });

    history.push('/profile');
  }

  perfil = () => {
    const { descricao, email, image, name, disabled } = this.state;
    return (
      <form>
        <div>
          <img src={ image || semImagem } alt={ name } />
        </div>
        <div>
          <label htmlFor="NomeUsuario">
            Nome
            <input
              type="text"
              id="NomeUsuario"
              placeholder="Seu nome de perfil"
              data-testid="edit-input-name"
              onChange={ this.handleChange }
              name="name"
              value={ name }
            />
          </label>
          <label htmlFor="EmailUsuario">
            Email
            <input
              type="text"
              id="EmailUsuario"
              placeholder="Seu email de cadastro"
              data-testid="edit-input-email"
              onChange={ this.handleChange }
              name="email"
              value={ email }
            />
          </label>
          <label htmlFor="DescricaolUsuario">
            Descrição
            <textarea
              id="DescricaolUsuario"
              placeholder="Uma breve descrição sobre você"
              data-testid="edit-input-description"
              onChange={ this.handleChange }
              name="descricao"
              value={ descricao }
            />
          </label>
          <label htmlFor="FotoUsuario">
            Imagem
            <input
              type="text"
              id="FotoUsuario"
              placeholder="Sua foto de perfil"
              data-testid="edit-input-image"
              onChange={ this.handleChange }
              name="image"
              value={ image }
            />
          </label>
        </div>
        <div>
          <button
            type="submit"
            data-testid="edit-button-save"
            disabled={ disabled }
            onClick={ this.handleClick }
          >
            Salvar
          </button>
        </div>
      </form>
    );
  }

  render() {
    const { carregando } = this.state;
    return (
      <div data-testid="page-profile-edit">
        <div>
          <Header />
        </div>
        <div>
          { carregando ? <Carregando /> : this.perfil() }
        </div>
        <p>ProfileEdit</p>
      </div>
    );
  }
}

ProfileEdit.propTypes = {
  history: PropTypes.object,
}.isRequired;

export default ProfileEdit;
