import React from 'react';
import PropTypes from 'prop-types';
import { addSong } from '../services/favoriteSongsAPI';
import Carregando from './Carregando';

class MusicCard extends React.Component {
    state = {
      carregando: false,
      checked: false,
    };

    addFavoriteSong = () => {
      const { musica } = this.props;
      console.log(musica);
      this.setState({ carregando: true }, async () => {
        await addSong(musica);
        this.setState({ carregando: false });
      });
    }

    // handleSong = ({ target }, callback) => {
    //   this.setState({ carregando: true, checked: target.checked });
    //   const { props: { musica, onChange } } = this;

    //   if (onChange) {
    //     onChange();
    //   }

    //   callback(musica).then(() => this.setState({ carregando: false }));
    // }

  listaMusicas = () => {
    const {
      props: { musica: { trackName, previewUrl, collectionName, trackId }, image },
      state: { checked },
    } = this;
    return (
      <div>
        <img src={ image } alt={ collectionName } />
        <p>{trackName}</p>
        <audio data-testid="audio-component" src={ previewUrl } controls>
          <track kind="captions" />
          O seu navegador n√£o suporta o elemento
          <code>
            audio
          </code>
          .
        </audio>
        <label htmlFor={ trackName }>
          <input
            type="checkbox"
            id={ trackName }
            data-testid={ `checkbox-music-${trackId}` }
            onClick={ this.addFavoriteSong }
            // onChange={ (event) => (this.handleSong(event, addSong)) }
            checked={ checked }
            // hidden
          />
          Favorita
        </label>
      </div>
    );
  }

  render() {
    const { carregando } = this.state;
    return (carregando ? <Carregando /> : this.listaMusicas());
  }
}

MusicCard.propTypes = {
  trackName: PropTypes.string,
  previewUrl: PropTypes.string,
}.isRequired;

export default MusicCard;
