import React from 'react';
import { Link } from 'react-router-dom';
import Header from '../components/Header';
import Carregando from '../components/Carregando';
import searchAlbumsAPI from '../services/searchAlbumsAPI';

class Search extends React.Component {
  state = {
    disabled: true,
    artistName: '',
    carregando: false,
    artistAlbuns: null,
  };

  countName = ({ target: { value } }) => {
    const sizeMin = 2;

    const artistName = value.split(' ')
      .map((name) => name.charAt(0).toUpperCase() + name.slice(1))
      .join(' ');

    this.setState({ artistName });

    if (value.length >= sizeMin) {
      this.setState({ disabled: false });
      this.name = artistName;
    }
  }

  handleSearch = () => {
    const { artistName } = this.state;

    this.setState({ carregando: true });

    searchAlbumsAPI(artistName).then((result) => {
      this.setState({
        carregando: false,
        artistAlbuns: result,
        artistName: '',
      });
    });
  }

  renderAlbuns = () => {
    const { artistAlbuns } = this.state;
    return (
      <section className="albuns-group">
        {
          artistAlbuns
            .map(({ collectionId, artistName, collectionName, artworkUrl100 }) => (
              <Link
                key={ collectionId }
                data-testid={ `link-to-album-${collectionId}` }
                to={ `/album/${collectionId}` }
              >
                <div>
                  <img src={ artworkUrl100 } alt={ collectionName } />
                  <h4>{artistName}</h4>
                  <h5>{collectionName}</h5>
                </div>
              </Link>
            ))
        }

      </section>
    );
  }

  checkAlbuns = () => {
    const { name, state: { artistAlbuns } } = this;
    if (artistAlbuns) {
      return (
        <>
          <h2>{`Resultado de álbuns de: ${name}`}</h2>
          { artistAlbuns.length > 0
            ? this.renderAlbuns()
            : <h3>Nenhum álbum foi encontrado</h3> }
        </>
      );
    }
  }

  render() {
    const { disabled, artistName, carregando } = this.state;
    return (
      <div data-testid="page-search">
        <Header />
        <label htmlFor="BuscarNomeArtista">
          <input
            value={ artistName }
            data-testid="search-artist-input"
            type="text"
            placeholder="Nome do artista"
            id="BuscarNomeArtista"
            className="BuscarNomeArtista"
            onChange={ this.countName }
          />
        </label>
        <button
          type="submit"
          className="BotaoProcurarArtista"
          data-testid="search-artist-button"
          disabled={ disabled }
          onClick={ this.handleSearch }
        >
          Pesquisar
        </button>
        { carregando ? <Carregando /> : this.checkAlbuns() }
        <p>Search</p>
      </div>
    );
  }
}

export default Search;
