import React from 'react';
import Header from '../components/Header';
import MusicCard from '../components/MusicCard';
import { getFavoriteSongs } from '../services/favoriteSongsAPI';
import Carregando from '../components/Carregando';

class Favorites extends React.Component {
  state = {
    musicas: '',
    carregando: true,
  };

  // componentDidMount() {
  //   this.updateFavoritesSongs();
  // }

  componentDidUpdate() {
    this.updateFavoritesSongs();
  }

  componentDidMount = async () => {
    // this.setState({ carregando: true });
    const musicas = await getFavoriteSongs();
    // const { name, email, description, image } = userInfo;
    this.setState({
      carregando: false,
      musicas,
      // image,
      // description,
      // email,
    });
  }

  updateFavoritesSongs = () => {
    const { carregando } = this.state;
    getFavoriteSongs().then((musicas) => {
      if (carregando) {
        this.setState(() => ({
          musicas,
          carregando: false,
        }));
      }
    });
  };

  handleChange = () => {
    this.setState({ carregando: true });
  };

  renderMusic = () => {
    const { musicas } = this.state;

    if (musicas) {
      return musicas.map((musica) => (
        <MusicCard
          musica={ musica }
          key={ musica.trackName }
          onChange={ this.handleChange }
          image={ musica.artworkUrl100 }
        />
      ));
    }
  };

  render() {
    const { musicas } = this.state;
    return (
      <div data-testid="page-favorites">
        <Header />
        <div>
          {!musicas ? <Carregando /> : this.renderMusic()}
        </div>
        <p>Favorites</p>
      </div>
    );
  }
}

export default Favorites;
