import React from 'react';
import { Redirect } from 'react-router';
import { createUser } from '../services/userAPI';
import Carregando from '../components/Carregando';

class Login extends React.Component {
  state = {
    disabled: true,
    userName: '',
    carregando: false,
    redirecionar: false,
  };

  countName = ({ target: { value } }) => {
    const sizeMin = 3;
    if (value.length >= sizeMin) {
      this.setState({ disabled: false, userName: value });
    }
  };

  getUser = () => {
    this.setState({
      carregando: true,
    });
    const { userName } = this.state;
    createUser({ name: userName }).then(() => {
      this.setState({
        carregando: false,
        redirecionar: true,
      });
    });
  };

  render() {
    const { carregando, redirecionar, disabled } = this.state;

    if (carregando) {
      return (
        <div>
          <Carregando />
        </div>
      );
    }

    if (redirecionar) {
      return <Redirect to="/search" />;
    }

    return (
      <div data-testid="page-login">
        <label htmlFor="NameLogin">
          <input
            data-testid="login-name-input"
            type="text"
            placeholder="Seu nome"
            id="NameLogin"
            className="NameLogin"
            onChange={ this.countName }
          />
        </label>
        <button
          type="submit"
          className="Form-btnSubmit"
          data-testid="login-submit-button"
          disabled={ disabled }
          onClick={ this.getUser }
        >
          Entrar
        </button>
      </div>
    );
  }
}

export default Login;

